
import React from 'react';

// A tiny, silent, looping video is the standard workaround for preventing
// screen sleep on iOS, as the Wake Lock API is not supported.
const silentVideoDataUri = "data:video/mp4;base64,";

const ScreenWaker: React.FC = () => {
  return (
    <video
      playsInline
      loop
      muted
      autoPlay
      src={silentVideoDataUri}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '1px',
        height: '1px',
        opacity: 0,
        pointerEvents: 'none',
        zIndex: -1,
      }}
    />
  );
};

export default ScreenWaker;
