import { useEffect, useRef } from 'react';

const isIOS = () => {
  // Проверка на iOS-устройства
  return [
    'iPad Simulator',
    'iPhone Simulator',
    'iPod Simulator',
    'iPad',
    'iPhone',
    'iPod'
  ].includes(navigator.platform) 
  // Обнаружение iPad на iOS 13+
  || (navigator.userAgent.includes("Mac") && "ontouchend" in document);
};

// Крошечное, беззвучное, зацикленное видео в формате base64
const silentVideoDataUri = "data:video/mp4;base64,";

/**
 * Хук для предотвращения засыпания экрана.
 * Он автоматически определяет наилучший доступный метод: Wake Lock API для поддерживаемых
 * браузеров или фоновое видео для iOS.
 */
export const useStayAwake = () => {
  const wakeLockSentinel = useRef<WakeLockSentinel | null>(null);
  const videoRef = useRef<HTMLVideoElement | null>(null);

  useEffect(() => {
    let isMounted = true;
    const canUseWakeLock = 'wakeLock' in navigator && !isIOS();

    // Функция для запроса Wake Lock
    const requestWakeLock = async () => {
      if (!isMounted || !canUseWakeLock || document.visibilityState !== 'visible') return;
      try {
        wakeLockSentinel.current = await navigator.wakeLock.request('screen');
        wakeLockSentinel.current.addEventListener('release', () => {
          // Если блокировка была снята (например, при сворачивании),
          // пытаемся получить ее заново, когда вкладка снова станет активной.
          console.log('Screen Wake Lock was released.');
          wakeLockSentinel.current = null;
        });
        console.log('Screen Wake Lock is active.');
      } catch (err: any) {
        console.error(`Failed to acquire screen wake lock: ${err.name}, ${err.message}`);
      }
    };
    
    // Обходной путь с видео для iOS
    const startVideoWakeLock = () => {
      if (videoRef.current) return;

      const video = document.createElement('video');
      video.setAttribute('playsinline', '');
      video.setAttribute('loop', '');
      video.setAttribute('muted', '');
      video.setAttribute('autoplay', '');
      video.style.position = 'fixed';
      video.style.top = '0';
      video.style.left = '0';
      video.style.width = '1px';
      video.style.height = '1px';
      video.style.opacity = '0';
      video.style.pointerEvents = 'none';
      video.style.zIndex = '-1';
      video.src = silentVideoDataUri;

      document.body.appendChild(video);
      videoRef.current = video;

      video.play().catch(error => {
          console.error("Silent video playback failed:", error);
      });
    };

    // Очистка видео-элемента
    const stopVideoWakeLock = () => {
        if (videoRef.current) {
            videoRef.current.remove();
            videoRef.current = null;
        }
    };
    
    // Обработчик изменения видимости страницы для повторного запроса блокировки
    const handleVisibilityChange = () => {
        if (canUseWakeLock && !wakeLockSentinel.current && document.visibilityState === 'visible') {
            requestWakeLock();
        }
    };

    // Основная логика при монтировании компонента
    if (canUseWakeLock) {
        requestWakeLock();
        document.addEventListener('visibilitychange', handleVisibilityChange);
    } else {
        startVideoWakeLock();
    }

    // Функция очистки при размонтировании
    return () => {
      isMounted = false;
      if (canUseWakeLock) {
        if (wakeLockSentinel.current) {
            wakeLockSentinel.current.release();
        }
        document.removeEventListener('visibilitychange', handleVisibilityChange);
      } else {
        stopVideoWakeLock();
      }
    };
  }, []); // Пустой массив зависимостей означает, что эффект выполнится один раз
};
